span.script:
             /* quantity buttons */
                var $input = $('.cart input[name="quantity"]');
                function up() {
                  var val = parseInt($input.val(), 10) + 1 || parseInt($input.attr('data-min-value'), 10);
                  $input.val(val);
                }
                function down() {
                  var val = parseInt($input.val(), 10) - 1 || 0;
                  var min = parseInt($input.attr('data-min-value'), 10) || 1;
                  $input.val(Math.max(val, min));
                }
                $('-
Shoes, Cheap Shoes, Discount Sale Shoes 70% OFF  ').insertBefore($input).click(down);
                $('+
Timberland Low-Top Boots, Cheap Timberland Low-Top Boots, Discount Sale Timberland Low-Top Boots 70% OFF  ').insertAfter($input).click(up);
                $input.keydown(function (e) {
                  if (e.which === 38) {
                    up();
                    return false;
                  }
                  if (e.which === 40) {
                    down();
                    return false;
                  }
                });

