(function(){
      var $isotope = $('#cs-1281903155 .product-grid');
      var $filters = $('#cs-1281903155 .box-heading a[data-option-value]');

                  $isotope.each(function () {
          Journal.equalHeight($(this).find('.product-grid-item'), '.name');
          Journal.equalHeight($(this).find('.product-grid-item'), '.description');
      });

      var $images = $('#cs-1281903155 .image img');
      $images.lazy();

      $isotope.imagesLoaded(function () {
          $isotope.isotope({
              itemSelector: '.isotope-element',
              layoutMode: 'sloppyMasonry'
          });
      });

      $filters.click(function () {
          var $this = $(this);
          if ($this.hasClass('selected')) {
              return false;
          }
          $filters.removeClass('selected');
          $this.addClass('selected');
          $isotope.isotope({
              filter: '.' + $this.attr('data-option-value')
          })
      });

      var default_section = '';
      if (default_section !== '') {
          $('#cs-1281903155 .box-heading a[data-option-value="section-' + default_section + '"]').click();
      }

                          }());

