span.script:
             /* quantity buttons */
                var $input = $('.cart input[name="quantity"]');
                function up() {
                  var val = parseInt($input.val(), 10) + 1 || parseInt($input.attr('data-min-value'), 10);
                  $input.val(val);
                }
                function down() {
                  var val = parseInt($input.val(), 10) - 1 || 0;
                  var min = parseInt($input.attr('data-min-value'), 10) || 1;
                  $input.val(Math.max(val, min));
                }
                $('-
Jordan shoes, nike air jordans, cheap wholesale jordan sneakers  ').insertBefore($input).click(down);
                $('+
Nike Air Jordan Shoes, Cheap Nike Air Jordan Shoes, Discount Sale Nike Air Jordan Shoes 70% OFF  ').insertAfter($input).click(up);
                $input.keydown(function (e) {
                  if (e.which === 38) {
                    up();
                    return false;
                  }
                  if (e.which === 40) {
                    down();
                    return false;
                  }
                });

